import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import styles from './NavBar.module.scss';
import Logo from '../../assets/CI_Logo.png';
import ProfOr from '../../assets/Prof_Or.png';

const NavBar = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  return (
    <div className={styles.navContainer}>
      <div className={styles.leftSection}>
        <div className={styles.logoContainer}>
          <img src={Logo} alt="Class Insight Logo" className={styles.logo} />
          <span className={styles.logoText}>CLASS INSIGHT</span>
        </div>
      </div>

      <div className={styles.centerSection}>
        <button 
          className={`${styles.navButton} ${location.pathname === '/new-assignment' ? styles.active : ''}`}
          onClick={() => navigate('/new-assignment')}
        >
          New Assignment
        </button>
        <button 
          className={`${styles.navButton} ${location.pathname === '/past-assignments' ? styles.active : ''}`}
          onClick={() => navigate('/past-assignments')}
        >
          Past Assignments
        </button>
        <button 
          className={`${styles.navButton} ${location.pathname === '/ai-settings' ? styles.active : ''}`}
          onClick={() => navigate('/ai-settings')}
        >
          AI Settings
        </button>
        <button 
          className={`${styles.navButton} ${location.pathname === '/classes' ? styles.active : ''}`}
          onClick={() => navigate('/classes')}
        >
          Classes
        </button>
      </div>

      <div className={styles.rightSection} onClick={toggleDropdown}>
        <div className={styles.courseInfo}>
          <span className={styles.courseName}>Business Association</span>
          <span className={styles.courseCode}>LAW100 B</span>
        </div>
        <img src={ProfOr} alt="Profile" className={styles.profilePic} />

        {isDropdownOpen && (
          <div className={styles.dropdown}>
            <div className={styles.dropdownItem}>Profile Settings</div>
            <div className={styles.dropdownItem}>Switch Class</div>
            <div className={styles.dropdownItem}>Log Out</div>
          </div>
        )}
      </div>
    </div>
  );
};

export default NavBar;
